// SPDX-License-Identifier: MIT
// solidity  truffle-config.js  compiler   .
pragma solidity ^0.8.13;

contract VendingMachine {
    // smart contract deployer 
    address public owner;
    mapping (address => uint) public donutBalances;

    // set owner, set donut balances   
    //  constructor smart contract deploy   
    //  deploy  address .
    constructor () {
        //msg is special global variable
        //and it has property called sender
        owner = msg.sender; //       etherium address
        
        // this current contract   
        donutBalances[address(this)] = 100; //owner address .
    }

//"view" means this function is not modify any data on the blockchain
//but can read data from the blockchain
//"pure"  modifying, reading     
    function getVendingMachineBalance () public view returns (uint) {
        return donutBalances[address(this)]; //  this contract(current contract) address  
    }

// number of items being added to the vending machine 
//block chain  modify  view pure  
//   return    
//, smart contract owner     => require 
//require    ,      
    function restock(uint amount) public {
        require(msg.sender == owner, "Only the owner can restock this machine.");
        donutBalances[address(this)] += amount;
    }

// payable keword is used for any function which needs to receive ether
// payable   ether   transaction  ether
//   . .
//      increment, vending machine   decrement .
//    (amount) 
    function purchase(uint amount) public payable {
        //  ether    msg.value 
        //     2 ether .  amount * 2 ether
        require(msg.value >= amount * 2 ether, "You must pay at least 2 ether per donut");
        require(donutBalances[address(this)] >= amount, "Not enough donuts in stock to fulfill purchase request");
        donutBalances[address(this)] -= amount;
        donutBalances[msg.sender] += amount; //sender purchase function call 
        //,   sender address  amount increment 

        // purchaser  ,     (   require)
        //    vending machine   ( require  require)
        //   2   4ether  .    .
        //4ether   1   .   4ether+  .
    }
}
